local f=function(name, en, ru)
	if RUWOW_EnableInterface then
		setglobal(name, ru)
	else
		setglobal(name, en)
	end
	RUWOW_Interface[en]=ru
end
local f1=function(name, text)
	if getglobal(name) and (getglobal(name):IsObjectType('Button') or getglobal(name):IsObjectType('FontString')) then
		getglobal(name):SetText(getglobal(text))
	end
end
